mapper.update:
update CodeGroup
SET
    codeGroupName = #{codeGroupName}
  ,codeGroupNameEng = #{codeGroupNameEng}
  ,codeGroupUseNY = #{codeGroupUseNY}
  ,codeGroupDelNY = #{codeGroupDelNY}
  ,codeGroupUpdate = now()
WHERE
    codeGroupSeq = #{codeGroupSeq}

mapper.delete:
delete from CodeGroup
where 1=1
  and codeGroupSeq = #{codeGroupSeq}

mapper.sql:
FROM CodeGroup
WHERE 1=1
AND codeGroupUseNY = #{shUseNy}
AND codeGroupDelNY = #{shDelNy}

    AND codeGroupRegdate BETWEEN #{shDateStart} AND #{shDateEnd}
    AND codeGroupUpdate BETWEEN #{shDateStart} AND #{shDateEnd}


    AND codeGroupSeq = #{shValue}
    AND codeGroupName LIKE CONCAT('%',#{shValue},'%')
    AND codeGroupNameEng LIKE CONCAT('%',#{shValue},'%')
