mapper.insert:
INSERT INTO infrCodeGroup (
    ifcgSeqAnother
    ,ifcgName
    ,ifcgNameEng
    ,ifcgUseNy
    ,ifcgOrder
    ,ifcgReferenceV1
    ,ifcgReferenceV2
    ,ifcgReferenceV3
    ,ifcgReferenceV4
    ,ifcgReferenceI1
    ,ifcgReferenceI2
    ,ifcgReferenceI3
    ,ifcgReferenceI4
    ,ifcgDesc
    ,ifcgDelNy
) VALUES (
    #{ifcgSeqAnother}
    ,#{ifcgName}
    ,#{ifcgNameEng}
    ,#{ifcgUseNy}
    ,#{ifcgOrder}
    ,#{ifcgReferenceV1}
    ,#{ifcgReferenceV2}
    ,#{ifcgReferenceV3}
    ,#{ifcgReferenceV4}
    ,#{ifcgReferenceI1}
    ,#{ifcgReferenceI2}
    ,#{ifcgReferenceI3}
    ,#{ifcgReferenceI4}
    ,#{ifcgDesc}
    ,0
    )

    SELECT last_insert_id()

mapper.update:
UPDATE infrCodeGroup
SET
    ifcgSeqAnother = #{ifcgSeqAnother}
    ,ifcgName = #{ifcgName}
    ,ifcgNameEng = #{ifcgNameEng}
    ,ifcgUseNy = #{ifcgUseNy}
    ,ifcgOrder = #{ifcgOrder}
    ,ifcgReferenceV1 = #{ifcgReferenceV1}
    ,ifcgReferenceV2 = #{ifcgReferenceV2}
    ,ifcgReferenceV3 = #{ifcgReferenceV3}
    ,ifcgReferenceV4 = #{ifcgReferenceV4}
    ,ifcgReferenceI1 = #{ifcgReferenceI1}
    ,ifcgReferenceI2 = #{ifcgReferenceI2}
    ,ifcgReferenceI3 = #{ifcgReferenceI3}
    ,ifcgReferenceI4 = #{ifcgReferenceI4}
    ,ifcgDesc = #{ifcgDesc}
WHERE 1=1
    AND ifcgSeq = #{ifcgSeq}

mapper.delete:
DELETE FROM infrCodeGroup
WHERE 1=1
  AND ifcgSeq = #{ifcgSeq}

mapper.sql:
FROM infrCodeGroup a
WHERE 1=1
    AND ifcgUseNy = #{shUseNy}
    AND ifcgDelNy = #{shDelNy}

        AND codeGroupRegdate BETWEEN #{shDateStart} AND #{shDateEnd}
        AND codeGroupUpdate BETWEEN #{shDateStart} AND #{shDateEnd}


        AND ifcgSeq = #{shValue}
        AND ifcgName LIKE CONCAT('%',#{shValue},'%')
        AND ifcgNameEng LIKE CONCAT('%',#{shValue},'%')
