mapper.sql:
FROM infrCode a
LEFT JOIN infrCodeGroup b on b.ifcgSeq = a.ifcgSeq
WHERE 1=1
AND ifcdUseNy = #{shUseNy}
AND ifcdDelNy = #{shDelNy}

    AND a.ifcgSeq = #{shValue}
    AND b.ifcgName LIKE CONCAT('%',#{shValue},'%')
    AND a.ifcdSeq = #{shValue}
    AND a.ifcdSeqAnother = #{shValue}
    AND a.ifcdName LIKE CONCAT('%',#{shValue},'%')
    AND a.ifcdNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrCode (
ifcdSeqAnother
,ifcdName
,ifcdNameEng
,ifcdNameLang1
,ifcdNameLang2
,ifcdNameLang3
,ifcdNameLang4
,ifcdNameLang5
,ifcdNameLang6
,ifcdUseNy
,ifcdOrder
,ifcdReferenceV1
,ifcdReferenceV2
,ifcdReferenceV3
,ifcdReferenceV4
,ifcdReferenceI1
,ifcdReferenceI2
,ifcdReferenceI3
,ifcdReferenceI4
,ifcdDesc
,ifcdDelNy
,ifcgSeq
)
VALUES (
#{ifcdSeqAnother}
,#{ifcdName}
,#{ifcdNameEng}
,#{ifcdNameLang1}
,#{ifcdNameLang2}
,#{ifcdNameLang3}
,#{ifcdNameLang4}
,#{ifcdNameLang5}
,#{ifcdNameLang6}
,#{ifcdUseNy}
,#{ifcdOrder}
,#{ifcdReferenceV1}
,#{ifcdReferenceV2}
,#{ifcdReferenceV3}
,#{ifcdReferenceV4}
,#{ifcdReferenceI1}
,#{ifcdReferenceI2}
,#{ifcdReferenceI3}
,#{ifcdReferenceI4}
,#{ifcdDesc}
,0
,#{ifcgSeq}
)

    SELECT last_insert_id()

mapper.update:
UPDATE infrCode
SET
ifcdSeqAnother = #{ifcdSeqAnother}
,ifcdName = #{ifcdName}
,ifcdNameEng = #{ifcdNameEng}
,ifcdNameLang1 = #{ifcdNameLang1}
,ifcdNameLang2 = #{ifcdNameLang2}
,ifcdNameLang3 = #{ifcdNameLang3}
,ifcdNameLang4 = #{ifcdNameLang4}
,ifcdNameLang5 = #{ifcdNameLang5}
,ifcdNameLang6 = #{ifcdNameLang6}
,ifcdUseNy = #{ifcdUseNy}
,ifcdOrder = #{ifcdOrder}
,ifcdReferenceV1 = #{ifcdReferenceV1}
,ifcdReferenceV2 = #{ifcdReferenceV2}
,ifcdReferenceV3 = #{ifcdReferenceV3}
,ifcdReferenceV4 = #{ifcdReferenceV4}
,ifcdReferenceI1 = #{ifcdReferenceI1}
,ifcdReferenceI2 = #{ifcdReferenceI2}
,ifcdReferenceI3 = #{ifcdReferenceI3}
,ifcdReferenceI4 = #{ifcdReferenceI4}
,ifcdDesc = #{ifcdDesc}
,ifcgSeq = #{ifcgSeq}
WHERE 1=1
AND ifcdSeq = #{ifcdSeq}

mapper.delete:
DELETE FROM infrCode
WHERE 1=1
  AND ifcdSeq = #{ifcdSeq}
