mapper.sql:
FROM infrMenu a
WHERE 1=1
AND ifmuUseNy = #{shUseNy}
AND ifmuDelNy = #{shDelNy}

    AND regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
    AND modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


    AND ifmuSeq = #{shValue}
    AND ifmuName LIKE CONCAT('%',#{shValue},'%')
    AND ifmuNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrMenu (
     ifmuName
    ,ifmuNameEng
    ,ifmuAdminNy
    ,ifmuUrl
    ,ifmuDepth
    ,ifmuParents
    ,ifmuUseNy
    ,ifmuOrder
    ,ifmuDesc
    ,ifmuDelNy
) VALUES (
     #{ifmuName}
    ,#{ifmuNameEng}
    ,#{ifmuAdminNy}
    ,#{ifmuUrl}
    ,#{ifmuDepth}
    ,#{ifmuParents}
    ,#{ifmuUseNy}
    ,#{ifmuOrder}
    ,#{ifmuDesc}
    ,0
)

    SELECT last_insert_id()

mapper.update:
UPDATE infrMenu
SET
  ifmuName = #{ifmuName}
  ,ifmuNameEng = #{ifmuNameEng}
  ,ifmuUrl = #{ifmuUrl}
  ,ifmuAdminNy = #{ifmuAdminNy}
  ,ifmuUseNy = #{ifmuUseNy}
  ,ifmuDepth = #{ifmuDepth}
  ,ifmuParents = #{ifmuParents}
  ,ifmuAdminNy = #{ifmuAdminNy}
  ,ifmuOrder = #{ifmuOrder}
  ,ifmuDesc = #{ifmuDesc}
WHERE 1=1
  AND ifmuSeq = #{ifmuSeq}

mapper.delete:
DELETE FROM infrMenu
WHERE 1=1
  AND ifmuSeq = #{ifmnSeq}
